import React from 'react';
import './Home.css';
import './Chat.css';

function Chat(){
    return (
        <div>
            <div className="ContainerT">
                <h1 className="Title1"> Chat </h1>
            </div>
            <div className="Txtb">
                <textarea id="texta" readOnly className="Chatb" />
            </div>
            <input id="inp" className="Searchc" placeholder="Type here" />
            <button id="sendb" onClick={enterMessage} className="Bttn" />
        </div>
       
    );
}

let enterButton = document.getElementById('sendb');
let box = document.getElementById('texta');
let inputChat = document.getElementById('inp');


//Ingresa el mensaje si presiona enter
inputChat.addEventListener("keyup", function (event) {
    if (event.keyCode === 13) {
        enterMessage();
    }
})

//Ingresa mensaje al textarea
function enterMessage() {
    var inVal = inputChat.value;
    if (inVal != "") {
        var today = new Date();
        var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
        let bt = document.createTextNode(inVal + " - " + time + "\n");
        box.appendChild(bt);
        inputChat.value = "";
        box.scrollTop = box.scrollHeight;       //Scroll se mantiene abajo
    }
}
export default Chat;
